import React from 'react'
import axios from 'axios'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faTimesCircle,faTrash } from '@fortawesome/free-solid-svg-icons'



const iconClose = <FontAwesomeIcon size="2x" color="red" icon={faTimesCircle} />
const iconTrash = <FontAwesomeIcon size="2x" color="black" icon={faTrash} />

const Done = ()=>{
   
    const [done, setDone] = React.useState([]);
    const [edit, setEdit] = React.useState(false);
    const TokenLocal = localStorage.getItem("token")

    
    React.useEffect(()=>{  
        fetchDoneTask();
    }, []);


    function  fetchDoneTask(){
        axios
            .get(`http://localhost:1000/api/task?token=${TokenLocal}&isFinished=true`)
            .then((res)=>{
                
                setDone(res.data)
                
            });
    }

    function  onUpdate(id,isFinished){
        axios.put(`http://localhost:1000/api/task?token=${TokenLocal}&id=${id}`,{
            isFinished : isFinished,
        }).then(()=>{
            
            fetchDoneTask();
        })
    }


    function onDelete(id){
        axios.delete(`http://localhost:1000/api/task?token=${TokenLocal}&id=${id}`)
        .then(()=>{ 
            fetchDoneTask();
        });
    }
    
    function maptask(){
        {done.map((task)=>(
            <center>
                <div className="col-md-6">
                    <div className="Card mb-sm-2" >
                       <div className="row">
                            <div className="col-md-6">
                                <div className="row">
                                    <span  style={{fontFamily:"Prompt"}} >{task.taskName} </span>
                                </div>
                                <div className="row">
                                    <span style={{fontFamily:"Prompt"}} >{task.time} </span>
                                </div> 
                            </div>
                            <div className="col-sm-6">
                                    <div className="row">
                                         <div className="col-sm-6" >
                                            
                                        </div> 
                                        <div className="col-sm-2" >
                                            <div  align="right" className="buttonRed" onClick={()=>onUpdate(task._id,false)}> {iconClose}</div>
                                        </div>   
                                        <div className="col-sm-2" >
                                            <div  align="right" className="buttonRed" onClick={()=>onUpdate(task._id,false)}> {iconClose}</div>
                                        </div>  
                                        <div className="col-sm-2" >
                                            <div  align="right" className="buttonRed" onClick={()=>onDelete(task._id)}>{iconTrash}</div>
                                        </div>   
                                    </div>
                            </div>
                            
                       </div>
                       
                       
                        
                    </div>
                </div>
            </center>
            
        ))}
    }
    
   

    
    return<div>
        <div>
            
            <ul className="mt-lg-5">
                
            {
             edit ? 
            (
                 <h7><div className="red">The password you entered is incorret</div></h7>
            )  
            : 
            (
                <h1></h1>
            )
            }

                <center>
                   
                         <h1 className="mb-md-5" style={{fontFamily:"Dela Gothic One"}}  >Done</h1>
                    
                </center>
               
                
            </ul>
          
        </div>
    </div>;
}
export default Done;
