import React from 'react'
import {Navbar, Nav, NavItem,Brand,Button,NavDropdown,Form,FormControl} from 'react-bootstrap';
import Login from  "./pages/Login"
import { BrowserRouter as Router, Switch, Route ,Link} from "react-router-dom";
import { useEffect, useState } from 'react';
import axios from 'axios'
import Component from "./component"
import BeforeSignIn from "./beforeSignIn"


const BeforeLogin = ()=>{
    
  const [Session,setSession] = useState({
    isLoggedin : false ,
    currentUser : null,
    errorMessage : null
  });

  const TokenLocal = localStorage.getItem("token")

  function  Check(req,res){
    axios
        .post(`http://localhost:1000/api/check?token=${TokenLocal}`)
        .then((res)=>{
           if(res.data==true)
           {
             setSession ({
               isLoggedin : true
             })
           }
        })
  }

  React.useEffect(()=>{
    Check();
  }, []);

  
  return (
    <div>
       
       {
        Session.isLoggedin ? 
        (
          <Component />
        )  
        : 
        (
          <Router>
              <Route exact path="/" component={() =>  <Login setSession={setSession}/>} />
              <Route path="/" component={() =>  <BeforeSignIn />}/>
          </Router>
        )
      }
    </div>
   
  );  

}
export default BeforeLogin;
