import React from 'react'
import axios from 'axios'
import NavbarTitle from "./Nav/Navbar" 
import {Navbar, Nav, NavItem,Brand,Button,NavDropdown,Form,FormControl} from 'react-bootstrap';

const Login = ({setSession})=>{
   
    const [createInputUser,setcreatInputUser] = React.useState("");
    const [createInputPassword,setcreatInputPassword] = React.useState("");
    

    async function  logIn(){
        axios
            .post("http://localhost:1000/api/",{
                UserID: createInputUser,
                Password: createInputPassword,
            }).then((res)=>{
                localStorage.setItem("token",res.data)
                console.log(res.data)
                setSession({
                    isLoggedin :true ,
                    currentUser : res.data
                });
            })
    }

    
    return <div>
        <NavbarTitle/>
        
        <div class="row">
            <div class="col-md-8">

            </div>
            
            <div class="col-md-3">
                <br></br><br></br>
                <h1>Login</h1>
                <input   type="User" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter User" value={createInputUser} onChange={(e) => {

                    setcreatInputUser(e.target.value);
                }} />
                <br></br>
                <input type="Password" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Password" value={createInputPassword} onChange={(e) => {

                    setcreatInputPassword(e.target.value);
                }} />
                
                <br></br><br></br>
                <center>
                   

                    <Button href="/" variant="outline-info" className="mr-sm-2" style={{fontFamily:"Nunito"}} onClick={() => {
                        logIn()
                    }}>Login</Button>
                </center>
               
                

            </div>
            
            
        </div>
      
        
        
        
    </div>;
    
   
}
export default Login;
