import React from 'react'
import axios from 'axios'
import "../style/Todo.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheckCircle,faTrash } from '@fortawesome/free-solid-svg-icons'


const iconCheck = <FontAwesomeIcon size="2x" color="green" icon={faCheckCircle} />
const iconTrash = <FontAwesomeIcon size="2x" color="black" icon={faTrash} />

const Todo = ()=>{
    const [todo,setTodo] = React.useState([]);
    const TokenLocal = localStorage.getItem("token")

    const [edit, setEdit] = React.useState(false);
    
    const [editData,setEditData] = React.useState({
        ID:"",
        Name : "" ,
        Time : ""
    });
    
    React.useEffect(()=>{
        fetchTodoTask(); 
    }, []);

    function  fetchTodoTask(){
        axios
            .get(`http://localhost:1000/api/task?token=${TokenLocal}&isFinished=false`)
            .then((res)=>{
                console.log(res.data)
                setTodo(res.data)
                
            });
    }


    function  onUpdate(id,isFinished){
        //console.log(id,isFinished)
        axios.put(`http://localhost:1000/api/task?token=${TokenLocal}&id=${id}`,{
            isFinished : isFinished,
        }).then(()=>{
            fetchTodoTask(); 
        })
    }

    function  onUpdateData(id,Name,Time){
        axios.put(`http://localhost:1000/api/task?token=${TokenLocal}&id=${id}`,{
            taskName : Name,
            time : Time
        }).then(()=>{
            
            fetchTodoTask();
        })
    }

    function onDelete(id){
        axios.delete(`http://localhost:1000/api/task?token=${TokenLocal}&id=${id}`)
        .then(()=>{
            fetchTodoTask(); 
        });
    }
    
    return<div>
       
        <div>


            
            <ul className="mt-lg-5">

                {
                    edit ?
                        (
                            <div>
                                <center>
                                    <div className="col-md-6">
                                        <div className="Card mb-sm-2" >
                                            <div className="row">
                                                <div className="col-md-6">
                                                    <div className="row">
                                                        <input type="User" className="form-control" placeholder="Enter User" value={editData.Name} onChange={(e) => {
                                                            setEditData({
                                                                ID: editData.ID,
                                                                Name: e.target.value,
                                                                Time: editData.Time
                                                            });
                                                        }} />
                                                    </div>
                                                    <div className="row">
                                                        <input className="form-control" value={editData.Time} type="time" onChange={(e) => {
                                                            setEditData({
                                                                ID: editData.ID,
                                                                Name: editData.Name,
                                                                Time: e.target.value
                                                            });
                                                        }} />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6">
                                                    <div className="row">
                                                        <div className="col-sm-8" >

                                                        </div>
                                                        <div className="col-sm-2" >
                                                            <div align="right" className="buttonRed" onClick={() => {
                                                                onUpdateData(editData.ID, editData.Name, editData.Time)
                                                                setEdit(false)
                                                            }}>{iconClose}</div>
                                                        </div>
                                                        <div className="col-sm-2" >
                                                            <div align="right" className="buttonRed" onClick={() => setEdit(false)}>{iconTrash}</div>
                                                        </div>
                                                    </div>
                                                </div>

                                            </div>



                                        </div>
                                    </div>
                                </center>
                            </div>
                        )
                        :
                        (
                            <div>

                            </div>
                        )
                }


                <center>
                   
                         <h1 className="mb-md-5" style={{fontFamily:"Dela Gothic One"}}  >Todo</h1>
                    
                </center>
               
                {todo.map((task)=>(
                    <center>
                        <div className="col-md-6">
                            <div className="Card mb-sm-2" >
                               <div className="row">
                                    <div className="col-md-6">
                                        <div className="row">
                                            <span  style={{fontFamily:"Prompt"}} >{task.taskName} </span>
                                        </div>
                                        <div className="row">
                                            <span style={{fontFamily:"Prompt"}} >{task.time} </span>
                                        </div> 
                                    </div>
                                    <div className="col-sm-6">
                                            <div className="row">
                                                 <div className="col-sm-8" >
                                                    
                                                </div>  
                                                <div className="col-sm-2" >
                                                    <div  align="right" className="buttonGreen" onClick={() => onUpdate(task._id, true)}> {iconCheck}</div>
                                                </div>  
                                                <div className="col-sm-2" >
                                                    <div  align="right" className="buttonRed" onClick={() => onDelete(task._id)}>{iconTrash}</div>
                                                </div>   
                                            </div>
                                    </div>
                                    
                               </div>
                               
                               
                                
                            </div>
                        </div>
                    </center>
                    
                ))}
            </ul>
        </div>
    </div>;
}
export default Todo;
