import React from 'react'
import axios from 'axios'
import "./Todo.css";

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faccog } from '@fortawesome/free-solid-svg-icons'

  
const iconCoffee = <FontAwesomeIcon size="6x" icon={cog} />

const Todo = ()=>{
    const [todo,setTodo] = React.useState([]);
    const TokenLocal = localStorage.getItem("token")
    
    React.useEffect(()=>{
        fetchTodoTask(); 
    }, []);

    function  fetchTodoTask(){
        axios
            .get(`http://localhost:1000/api/task?token=${TokenLocal}&isFinished=false`)
            .then((res)=>{
                console.log(res.data)
                setTodo(res.data)
                
            });
    }


    function  onUpdate(id,isFinished){
        //console.log(id,isFinished)
        axios.put(`http://localhost:1000/api/task?token=${TokenLocal}&id=${id}`,{
            isFinished : isFinished,
        }).then(()=>{
            fetchTodoTask(); 
        })
    }

    function onDelete(id){
        axios.delete(`http://localhost:1000/api/task?token=${TokenLocal}&id=${id}`)
        .then(()=>{
            fetchTodoTask(); 
        });
    }
    
    return<div>
        {iconCoffee}
        <div>
            <ul className="mt-lg-5">
                <center>
                    <div className="col-md-6 mb-sm-4">
                         <h1 style={{fontFamily:"Dela Gothic One"}}  >Todo</h1>
                    </div>
                </center>
               
                {todo.map((task)=>(
                    <center>
                        <div className="col-md-6">
                            <div className="Card mb-sm-2" >
                               <div className="row">
                                    <div className="col-md-6">
                                        <div className="row">
                                            <span  style={{fontFamily:"Prompt"}} >{task.taskName} </span>
                                        </div>
                                        <div className="row">
                                            <span style={{fontFamily:"Prompt"}} >{task.time} </span>
                                        </div> 
                                    </div>
                                    <div className="col-md-6">
                                        <div align = 'right'>
                                            <button  onClick={() => onUpdate(task._id, true)}>âœ…</button>
                                            <button  onClick={() => onDelete(task._id)}>ðŸš®</button>
                                        </div>
                                        
                                    </div>
                                    
                               </div>
                               
                               
                                
                            </div>
                        </div>
                    </center>
                    
                ))}
            </ul>
        </div>
    </div>;
}
export default Todo;
