import React from 'react'
import axios from 'axios'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faUserCircle,faUnlock } from '@fortawesome/free-solid-svg-icons'


const iconUser = <FontAwesomeIcon size="10x" color="gray" icon={faUserCircle} />


const Profile = ()=>{
    const [name,setname] = React.useState([]);
    const [role,setrole] = React.useState([]);
    const TokenLocal = localStorage.getItem("token")
    function  Data(req,res){
        axios
            .get(`http://localhost:1000/api/user?token=${TokenLocal}`)
            .then((res)=>{
                setname(res.data.Name)
                setrole(res.data.role)
            })
    }
    
    React.useEffect(()=>{
        Data();
    }, []);
    
    return <div>
       
        <div  className="row">
            <div className="col-md-5">
            </div>
            <div className="col-md-3 mt-md-5">
                <div>
                    {iconUser}
                </div>
                <h1 align="center" style={{fontFamily:"Prompt"}}>Profile</h1>
                <h5 style={{fontFamily:"Prompt"}}>NAME : {name}</h5>
                <h5 style={{fontFamily:"Prompt"}}>Role : {role}</h5>
            </div>
        </div>
        
       
    </div>;
    
}
export default Profile;
