import React from 'react'
import axios from 'axios'
import NavbarTitle from "../Nav/Navbar" 
import {Navbar, Nav, NavItem,Brand,Button,NavDropdown,Form,FormControl} from 'react-bootstrap';
import "../style/signIn.css";


const SignIn = ({setSession})=>{
   
    const [createInputName,setcreatInputName] = React.useState("");
    const [createInputUser,setcreatInputUser] = React.useState("");
    const [createInputPassword,setcreatInputPassword] = React.useState("");
    const [UserSame,setUserSame] = React.useState(false);
    

      function  signin(){
        axios
            .post("http://localhost:1000/api/signIn",{
                Name: createInputName,
                UserID: createInputUser,
                Password: createInputPassword,
            }).then((res)=>{
                console.log(res.data)
                localStorage.setItem("token",res.data)
                setUserSame({
                    isSame  :false 
                });  
                setSession({
                    isSignIn  :true 
                });
                
            })
    }

    
    return <div>
        <NavbarTitle/>
        <body>
            <section className="Wave">
                <div  className="row">
                    <div className="col-md-8">
                       
                    </div>
                
                   
                    <div className="col-md-4">
                        <br></br><br></br><br></br><br></br>
                        <center>
                        <div className="Modal" >
                            <h1>SignIn</h1>
                            <br></br>
                            <h6 align = 'left'>Name :</h6>
                            <input type="Name" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Name" value={createInputName} onChange={(e) => {

                                setcreatInputName(e.target.value);
                            }} />
                            <br></br>
                            <h6 align = 'left'>User ID :</h6>
                            <input type="User" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter User" value={createInputUser} onChange={(e) => {

                                setcreatInputUser(e.target.value);
                            }} />
                            <br></br>
                            <h6 align = 'left'>Password :</h6>
                            <input type="Password" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Password" value={createInputPassword} onChange={(e) => {

                                setcreatInputPassword(e.target.value);
                            }} />
                            <br></br>
                            <Button variant="outline-info" className="mr-sm-2" style={{ fontFamily: "Nunito" }} onClick={() => {
                                signin()
                            }}>Create Account</Button>

                            {
                            UserSame.isSame ? 
                            (
                                <h1>Don't use this user</h1>
                            )  
                            : 
                            (
                                <h1></h1>
                            )
                            }     
                       </div>
                       </center>   
                    </div>   
                </div>
               
               
                    
                    
                
            </section>
        </body>

       
             
    </div>;
    
}
export default SignIn;
