import React from 'react'
import Login from  "./Login"
import { BrowserRouter as Router, Route} from "react-router-dom";
import { useEffect, useState } from 'react';
import axios from 'axios'
import Component from "./component"
import BeforeSignIn from "./beforeSignIn"


const BeforeLogin = ()=>{
    
  const [Session,setSession] = useState({
    isLoggedin : false ,
    currentUser : null,
    errorMessage : null
  });

  const TokenLocal = localStorage.getItem("token")

  function  Check(){
    if(TokenLocal==null)
    {
      console.log("null")
    }
    else{
      axios
      .post(`http://localhost:1000/api/check?token=${TokenLocal}`)
      .then((res)=>{
         if(res.data==true)
         {
           setSession ({
             isLoggedin : true
           })
         }
      })
    }

  }

  React.useEffect(()=>{
    Check();
  }, []);

  
  return (
    <div>
       
       {
        Session.isLoggedin ? 
        (
          <Component />
        )  
        : 
        (
          <Router>
              <Route exact path="/" component={() =>  <Login setSession={setSession}/>} />
              <Route path="/signIn" component={() =>  <BeforeSignIn />}/>
              <Route path="/done" component={() =>  <BeforeSignIn />}/>
            
          </Router>
        )
      }
    </div>
   
  );  

}
export default BeforeLogin;
