import React from 'react'
import {Navbar, Nav, NavItem,Brand,Button,NavDropdown,Form,FormControl} from 'react-bootstrap';
import axios from 'axios'


const Task = ()=>{
    const [todo,setTodo] = React.useState([]);
    const [done, setDone] = React.useState([]);
    const [createInput,setcreatInput] = React.useState("");
    const [createTime,setcreatTime] = React.useState("");
    const TokenLocal = localStorage.getItem("token")
    
    React.useEffect(()=>{
        fetchTodoTask(); 
        fetchDoneTask();
    }, []);

    function  fetchTodoTask(){
        console.log("555")
        axios
            .get(`http://localhost:1000/api/task?token=${TokenLocal}&isFinished=false`)
            .then((res)=>{
                console.log(res.data)
                setTodo(res.data)
                
            });
    }

    function  fetchDoneTask(){
        axios
            .get(`http://localhost:1000/api/task?token=${TokenLocal}&isFinished=true`)
            .then((res)=>{
                console.log(res.data)
                setDone(res.data)
                
            });
    }

    function  onCreate(){
        axios
            .post("http://localhost:1000/api//task",{
                token : TokenLocal,     
                taskName: createInput,
                time: createTime,
            }).then(()=>{
                setcreatInput("");
                setcreatTime("");
                fetchTodoTask(); 
            })
    }
    
    return<div>
        <div>
            <h1>Todo</h1>
            <ul>
                {todo.map((task)=>(
                    <li>
                        <span>{task.taskName} </span>
                        <span>{task.time} </span>
                        {/* <button onClick={()=>onUpdate(task._id,true)}>✅</button> */}
                        {/* <button onClick={()=>onDelete(task._id)}>🚮</button> */}
                    </li>
                ))}
            </ul>
        </div>

        <div>
            <h1>Done</h1>
            <ul>
                {done.map((task)=>(
                    <li>
                        <span>{task.taskName } </span>
                        <span>{task.time} </span>
                        {/* <button onClick={()=>onUpdate(task._id,false)}>❌</button> */}
                        {/* <button onClick={()=>onDelete(task._id)}>🚮</button> */}
                    </li>
                ))}
            </ul>
        </div>

        <div>
            <h1>Create</h1>
            <input value={createInput} onChange={(e)=>{
               
                setcreatInput(e.target.value);
            } }/>
            <input value={createTime} type="time" onChange={(e)=> setcreatTime(e.target.value)}/>
            <button onClick={()=> onCreate()}>Create</button>
        </div>

    </div>;
}
export default Task;
