import React from 'react'
import axios from 'axios'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faTimesCircle,faTrash,faPencilAlt,faCheckSquare,faTimes} from '@fortawesome/free-solid-svg-icons'



const iconClose = <FontAwesomeIcon size="2x" color="red" icon={faTimesCircle} />
const iconTrash = <FontAwesomeIcon size="2x" color="grey" icon={faTrash} />
const iconEdit = <FontAwesomeIcon size="2x" color="orange" icon={faPencilAlt} />
const iconSave = <FontAwesomeIcon size="3x" color="green" icon={faCheckSquare} />
const iconCancel = <FontAwesomeIcon size="3x" color="red" icon={faTimes} />


const Done = ()=>{
   
    const [done, setDone] = React.useState([]);
    const [edit, setEdit] = React.useState(false);
    
    const [editData,setEditData] = React.useState({
        ID:"",
        Name : "" ,
        Time : ""
    });
    
    const TokenLocal = localStorage.getItem("token")

    
    React.useEffect(()=>{  
        fetchDoneTask();
    }, []);


    function  fetchDoneTask(){
        axios
            .get(`http://localhost:1000/api/task?token=${TokenLocal}&isFinished=true`)
            .then((res)=>{
                
                setDone(res.data)
                
            });
    }

    function  onUpdate(id,isFinished){
        axios.put(`http://localhost:1000/api/task?token=${TokenLocal}&id=${id}`,{
            isFinished : isFinished,
        }).then(()=>{
            
            fetchDoneTask();
        })
    }

    function  onUpdateData(id,Name,Time){
        axios.put(`http://localhost:1000/api/task?token=${TokenLocal}&id=${id}`,{
            taskName : Name,
            time : Time
        }).then(()=>{
            
            fetchDoneTask();
        })
    }


    function onDelete(id){
        axios.delete(`http://localhost:1000/api/task?token=${TokenLocal}&id=${id}`)
        .then(()=>{ 
            fetchDoneTask();
        });
    }
    
    
   

    
    return<div>
        <div>
            
            <ul className="mt-lg-5">
                
            {
             edit ? 
            (
                            <div>
                                
                                <center>

                                    <h1 className="mb-md-5" style={{ fontFamily: "Dela Gothic One" }}  >Edit</h1>

                                </center>
                                <center>
                                    <div className="col-md-6">
                                        <div className="Card mb-sm-2" >
                                            <div className="row">
                                                <div className="col-md-6">
                                                    <div className="row">
                                                        <input type="User" className="form-control" placeholder="Enter User" value={editData.Name} onChange={(e) => {
                                                            setEditData ({
                                                                ID : editData.ID,
                                                                Name : e.target.value,
                                                                Time : editData.Time
                                                            });
                                                        }} />
                                                    </div>
                                                    <div className="row">
                                                        <input className="form-control" value={editData.Time} type="time" onChange={(e) => {
                                                            setEditData ({
                                                                ID : editData.ID,
                                                                Name : editData.Name,
                                                                Time : e.target.value
                                                            });
                                                        }} />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6">
                                                    <div className="row">
                                                        <div className="col-sm-8" >

                                                        </div>
                                                        <div className="col-sm-2  mr-md-2 ml-md-2" >
                                                            <div align="right" className="buttonGrey" onClick={() =>{
                                                                onUpdateData(editData.ID,editData.Name,editData.Time)
                                                                setEdit(false)
                                                            }}>{iconSave}</div>
                                                        </div>
                                                        <div className="col-sm-2  mr-md-2 ml-md-2" >
                                                            <div align="right" className="buttonGrey" onClick={() => setEdit(false)}>{iconCancel}</div>
                                                        </div>
                                                    </div>
                                                </div>

                                            </div>



                                        </div>
                                    </div>
                                </center>
                            </div>
                        )
                        :
                        (

                            <div>

                                <center>

                                    <h1 className="mb-md-5" style={{ fontFamily: "Dela Gothic One" }}  >Done</h1>

                                </center>


                                {done.map((task) => (
                                    <center>
                                        <div className="col-md-6">
                                            <div className="Card mb-sm-2" >
                                                <div className="row">
                                                    <div className="col-md-6">
                                                        <div className="row">
                                                            <span style={{ fontFamily: "Prompt" }} >{task.taskName} </span>
                                                        </div>
                                                        <div className="row">
                                                            <span style={{ fontFamily: "Prompt" }} >{task.time} </span>
                                                        </div>
                                                    </div>
                                                    <div className="col-sm-6">
                                                        <div className="row">
                                                            <div className="col-sm-6" >

                                                            </div>
                                                            <div className="col-sm-2" >
                                                                <div align="right" className="buttonGrey" onClick={() => onUpdate(task._id, false)}> {iconClose}</div>
                                                            </div>
                                                            <div className="col-sm-2" >
                                                                <div align="right" className="buttonGrey" onClick={() => {

                                                                    setEdit(true);
                                                                    setEditData ({
                                                                        ID : task._id,
                                                                        Name : task.taskName,
                                                                        Time : task.time
                                                                    });
                                                                    
                                                                }}> {iconEdit}</div>
                                                            </div>
                                                            <div className="col-sm-2" >
                                                                <div align="right" className="buttonGrey" onClick={() => onDelete(task._id)}>{iconTrash}</div>
                                                            </div>
                                                        </div>
                                                    </div>

                                                </div>



                                            </div>
                                        </div>
                                    </center>

                                ))}
                            </div>
            )
            }

               
                
            </ul>
          
        </div>
    </div>;
}
export default Done;
